import React from "react";
import ReactDOM from "react-dom";
import { Button, View, Text, FormLabel, Input } from 'react-native-elements';

import "./styles.css";

import useFormValidation from "./useFormValidation";
import validateAuth from "./validateAuth";

const INITIAL_STATE = {
  email: "",
  password: ""
};

function Register() {
  const {
    handleSubmit,
    handleChange,
    handleBlur,
    values,
    errors,
    isSubmitting
  } = useFormValidation(INITIAL_STATE, validateAuth);
  // const [email, setEmail] = React.useState("");
  // const [password, setPassword] = React.useState("");

  return (
    <View className="container">
      <Text>Register Here</Text>
      <FormLabel onSubmit={handleSubmit}>
        <Input
          onChange={handleChange}
          onBlur={handleBlur}
          name="email"
          value={values.email}
          className={errors.email && "error-input"}
          autoComplete="off"
          placeholder="Your email address"
        />
        {errors.email && <Text className="error-text">{errors.email}</Text>}
        <Input
          onChange={handleChange}
          onBlur={handleBlur}
          value={values.password}
          className={errors.password && "error-input"}
          name="password"
          type="password"
          placeholder="Choose a safe password"
        />
        {errors.password && <Text className="error-text">{errors.password}</Text>}
        <View>
          <Button disabled={isSubmitting} type="submit">
            Submit
          </Button>
        </View>
      </FormLabel>
    </View>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Register />, rootElement);
