import React from "react";

export default class Recipe extends React.Component {
	state = { results: null };
	render() {
		var name = "";
		var search = this.props.location.search;
		var params = new URLSearchParams(search);
		name = params.get('name');
		
		var jsonPayload = '{"Recipe" : "' + name + '"}';
		
		fetch('http://192.168.56.1:5000/api/viewRecipe', {
			method: 'POST',
			headers: {
				'Content-type': 'application/json',
			},
			body: jsonPayload,
		})
		.then(response => response.json())
		.then(data => {
			console.log('Success:', data);
			if (data.error == "") {
				this.setState({ results: data });
				document.getElementById("apireturn").innerHTML = data;
			}
			else {
				document.getElementById("apireturn").innerHTML = data;
			}
		})
		.catch((error) => {
			console.error('Error:', error);
			document.getElementById("apireturn").innerHTML = error;
		});
		
		var data = this.state.results;
		const viewer = []
		
		if (data != null) {
			viewer.push(
				<table className="viewerTable">
					<thead className="header">
						<tr>
							<th>{name}</th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td>
								{data.results.Ingredients}
							</td>
						</tr>
						<tr>
							<td>
								{data.results.Procedure}
							</td>
						</tr>
					</tbody>
				</table>
			)
		}
		
		return (
			<div>
				<h1>The Recipe Page</h1>
				<p>Recipe Name: { name }</p>
				{viewer}
				<div className="container">
					<h1>API Return</h1>
					<textarea
						id="apireturn"
						name="apireturn"
						row="4"
						cols="50"
					/>
				</div>
			</div>
		);
	}
}
