import React from "react";

export default class Reset extends React.Component {
	render() {
		var id = 0;
		var search = this.props.location.search;
		var params = new URLSearchParams(search);
		id = params.get('id');
		const handleSubmit = async e => {
			e.preventDefault();
			
			document.getElementById("passwordError").innerHTML = "";
			var lPassword = document.getElementById("loginpassword").value;
			if (lPassword == "") {
				document.getElementById("passwordError").innerHTML = "Please enter a password.";
				return;
			}
			
			var jsonPayload = '{"userId" : "' + id + '", "password" : "' + lPassword + '"}';
			
			fetch('http://192.168.56.1:5000/api/reset', {
				method: 'POST',
				headers: {
					'Content-type': 'application/json',
				},
				body: jsonPayload,
			})
			.then(response => response.json())
			.then(data => {
				console.log('Success:', data);
				if (data.error == "") {
					this.setState({ redirect: "/" });
				}
				else {
					document.getElementById("errorReturn").innerHTML = data.error;
				}
			})
			.catch((error) => {
				console.error('Error:', error);
				document.getElementById("errorReturn").innerHTML = data.error;
				return;
			});
		};
		
		return (
			<div>
				<form>
					<input
					 id="loginpassword"
					 name="loginpassword"
					 type="password"
					 placeholder="Your new password"
					/>
					<p className="passwordError" id="passwordError" />
					<div>
						<button
						 id="resetButton"
						 onClick={handleSubmit}
						 type="submit">
							Reset Password
						</button>
					</div>
				</form>
			</div>
		);
	}
}
