import React from "react";
import ReactDOM from "react-dom";

export default class User extends React.Component {
	state = { recipeResults: null, ingredientResults: null };
	render() {
		const handleIngredients = async e => {
			e.preventDefault();
			console.log('You clicked search!');
			
			var ingredient = document.getElementById("ingredientname").value;
			
			document.getElementById("ingredientname").innerHTML = "";
			
			var jsonPayload = '{"ingredient" : "' + ingredient + '"}';
			
			document.getElementById("apireturn").innerHTML = jsonPayload;
		}
		
		const handleRecipes = async e => {
			e.preventDefault();
			console.log('You clicked search but different!');
			
			var ingredient = document.getElementById("recipename").value;
			
			document.getElementById("recipename").innerHTML = "";
			
			var jsonPayload = '{"Recipe" : "' + ingredient + '"}';
			
			fetch('http://192.168.56.1:5000/api/searchRecipe', {
				method: 'POST',
				headers: {
					'Content-type': 'application/json',
				},
				body: jsonPayload,
			})
			.then(response => response.json())
			.then(data => {
				console.log('Success:', data);
				if (data.error == "") {
					document.getElementById("apireturn").innerHTML = data;
					this.setState({ recipeResults: data });
				}
				else {
					document.getElementById("apireturn").innerHTML = data.error;
				}
			})
			.catch((error) => {
				console.error('Error:', error);
				document.getElementById("apireturn").innerHTML = error;
				return;
			});
		};
		
		//recipe search results
		if (this.state.recipeResults) {
			return (
				<div className="userPage">
				<div>
					<div className="container">
						<h1>Recipe Search</h1>
						<form>
							<input
							 id="recipename"
							 name="recipename"
							 autoComplete="off"
							 placeholder="Search Term"
							/>
							<div>
								<button
								 type="button"
								 onClick={handleRecipes}
								 type="submit">
									Search
								</button>
								<table className="resultTable">
									<Recipes data={this.state.recipeResults} i={0} />
								</table>
							</div>
						</form>
					</div>
					<div className="container">
						<h1>Ingredient Search</h1>
						<form>
							<input
							 id="ingredientname"
							 name="ingredientname"
							 autoComplete="off"
							 placeholder="Search Term"
							/>
							<div>
							<button
							 type="button"
							 onClick={handleIngredients}
							 type="submit">
								Search
							</button>
							</div>
						</form>
					</div>
				</div>
				<div>
					<APIreturn />
				</div>
			</div>
			);
		}

		//no results
		return (
			<div className="userPage">
				<div>
					<div className="container">
						<h1>Recipe Search</h1>
						<form>
							<input
							 id="recipename"
							 name="recipename"
							 autoComplete="off"
							 placeholder="Search Term"
							/>
							<div>
							<button
							 type="button"
							 onClick={handleRecipes}
							 type="submit">
								Search
							</button>
							</div>
						</form>
					</div>
					<div className="container">
						<h1>Ingredient Search</h1>
						<form>
							<input
							 id="ingredientname"
							 name="ingredientname"
							 autoComplete="off"
							 placeholder="Search Term"
							/>
							<div>
							<button
							 type="button"
							 onClick={handleIngredients}
							 type="submit">
								Search
							</button>
							</div>
						</form>
					</div>
				</div>
				<div>
					<APIreturn />
				</div>
			</div>
		);
	}
}

function APIreturn() {
	return (
		<div className="container">
			<h1>API Return</h1>
			<textarea
				id="apireturn"
				name="apireturn"
				row="4"
				cols="50"
			/>
		</div>
	);
}

function Recipes(data, i) {
	if (i >= data.results.length) return;
	return (
		<div>
			<tr id={i}>
				<td>{data.results[i].Recipe}</td>
			</tr>
			<Recipes data={data} i={i+1} />
		</div>
	);
}
